svg.script:
		var pandaCredits = new CreditRoll();
		var pandaCreditsDictionary = new CreditDictionary();
		function initCredits(evt) {
			//Prep CreditsDictionary
			var dcCreatorLabel =     "created by";
			var dcContributorLabel = "with contributions by";
			var dcTitleLabel =       "title";
			var ccAgentLabel =       "person";
			var dcDateLabel =          "copyright";

			addDCLookups(pandaCreditsDictionary);
			addCCLookups(pandaCreditsDictionary);
			pandaCreditsDictionary.setLabel(dcNS,dcCreator,dcCreatorLabel);
			pandaCreditsDictionary.setLabel(dcNS,dcContributor,dcContributorLabel);
			pandaCreditsDictionary.setLabel(dcNS,dcTitle,dcTitleLabel);
			pandaCreditsDictionary.setLabel(ccNS,ccAgent,ccAgentLabel);
			pandaCreditsDictionary.setLabel(dcNS,dcDate,dcDateLabel);
			
			//Prep Credits
			pandaCredits.appendElementsByTagNameNS(dcNS,dcCreator,pandaCreditsDictionary);
			//pandaCredits.appendElementsByTagNameNS(dcNS,dcContributor,pandaCreditsDictionary);
			pandaCredits.appendElementsByTagNameNS(dcNS,dcDate,pandaCreditsDictionary);
			pandaCredits.assembleCredits();

			
		};
